package v1alpha1


// +kcc:proto=google.cloud.baremetalsolution.v2.VolumeSnapshot
type VolumeSnapshot struct {
	// The name of the snapshot.
	// +kcc:proto:field=google.cloud.baremetalsolution.v2.VolumeSnapshot.name
	Name *string `json:"name,omitempty"`

	// The description of the snapshot.
	// +kcc:proto:field=google.cloud.baremetalsolution.v2.VolumeSnapshot.description
	Description *string `json:"description,omitempty"`
}

// +kcc:proto=google.cloud.baremetalsolution.v2.VolumeSnapshot
type VolumeSnapshotObservedState struct {
	// Output only. An identifier for the snapshot, generated by the backend.
	// +kcc:proto:field=google.cloud.baremetalsolution.v2.VolumeSnapshot.id
	ID *string `json:"id,omitempty"`

	// Output only. The creation time of the snapshot.
	// +kcc:proto:field=google.cloud.baremetalsolution.v2.VolumeSnapshot.create_time
	CreateTime *string `json:"createTime,omitempty"`

	// Output only. The name of the volume which this snapshot belongs to.
	// +kcc:proto:field=google.cloud.baremetalsolution.v2.VolumeSnapshot.storage_volume
	StorageVolume *string `json:"storageVolume,omitempty"`

	// Output only. The type of the snapshot which indicates whether it was
	//  scheduled or manual/ad-hoc.
	// +kcc:proto:field=google.cloud.baremetalsolution.v2.VolumeSnapshot.type
	Type *string `json:"type,omitempty"`
}
