package v1alpha1


// +kcc:proto=google.cloud.dialogflow.cx.v3.NluSettings
type NluSettings struct {
	// Indicates the type of NLU model.
	// +kcc:proto:field=google.cloud.dialogflow.cx.v3.NluSettings.model_type
	ModelType *string `json:"modelType,omitempty"`

	// To filter out false positive results and still get variety in matched
	//  natural language inputs for your agent, you can tune the machine learning
	//  classification threshold. If the returned score value is less than the
	//  threshold value, then a no-match event will be triggered. The score values
	//  range from 0.0 (completely uncertain) to 1.0 (completely certain). If set
	//  to 0.0, the default of 0.3 is used. You can set a separate classification
	//  threshold for the flow in each language enabled for the agent.
	// +kcc:proto:field=google.cloud.dialogflow.cx.v3.NluSettings.classification_threshold
	ClassificationThreshold *float32 `json:"classificationThreshold,omitempty"`

	// Indicates NLU model training mode.
	// +kcc:proto:field=google.cloud.dialogflow.cx.v3.NluSettings.model_training_mode
	ModelTrainingMode *string `json:"modelTrainingMode,omitempty"`
}

// +kcc:proto=google.cloud.dialogflow.cx.v3.Version
type Version struct {
	// Format:
	//  projects/<ProjectID>/locations/<LocationID>/agents/<AgentID>/flows/<FlowID>/versions/<VersionID>.
	//  Version ID is a self-increasing number generated by Dialogflow upon version
	//  creation.
	// +kcc:proto:field=google.cloud.dialogflow.cx.v3.Version.name
	Name *string `json:"name,omitempty"`

	// Required. The human-readable name of the version. Limit of 64 characters.
	// +kcc:proto:field=google.cloud.dialogflow.cx.v3.Version.display_name
	DisplayName *string `json:"displayName,omitempty"`

	// The description of the version. The maximum length is 500 characters. If
	//  exceeded, the request is rejected.
	// +kcc:proto:field=google.cloud.dialogflow.cx.v3.Version.description
	Description *string `json:"description,omitempty"`
}

// +kcc:proto=google.cloud.dialogflow.cx.v3.Version
type VersionObservedState struct {
	// Output only. The NLU settings of the flow at version creation.
	// +kcc:proto:field=google.cloud.dialogflow.cx.v3.Version.nlu_settings
	NluSettings *NluSettings `json:"nluSettings,omitempty"`

	// Output only. Create time of the version.
	// +kcc:proto:field=google.cloud.dialogflow.cx.v3.Version.create_time
	CreateTime *string `json:"createTime,omitempty"`

	// Output only. The state of this version. This field is read-only and cannot
	//  be set by create and update methods.
	// +kcc:proto:field=google.cloud.dialogflow.cx.v3.Version.state
	State *string `json:"state,omitempty"`
}
