package v1alpha1


// +kcc:proto=google.monitoring.v3.Snooze
type Snooze struct {
	// Required. Identifier. The name of the `Snooze`. The format is:
	//
	//      projects/[PROJECT_ID_OR_NUMBER]/snoozes/[SNOOZE_ID]
	//
	//  The ID of the `Snooze` will be generated by the system.
	// +kcc:proto:field=google.monitoring.v3.Snooze.name
	Name *string `json:"name,omitempty"`

	// Required. This defines the criteria for applying the `Snooze`. See
	//  `Criteria` for more information.
	// +kcc:proto:field=google.monitoring.v3.Snooze.criteria
	Criteria *Snooze_Criteria `json:"criteria,omitempty"`

	// Required. The `Snooze` will be active from `interval.start_time` through
	//  `interval.end_time`.
	//  `interval.start_time` cannot be in the past. There is a 15 second clock
	//  skew to account for the time it takes for a request to reach the API from
	//  the UI.
	// +kcc:proto:field=google.monitoring.v3.Snooze.interval
	Interval *TimeInterval `json:"interval,omitempty"`

	// Required. A display name for the `Snooze`. This can be, at most, 512
	//  unicode characters.
	// +kcc:proto:field=google.monitoring.v3.Snooze.display_name
	DisplayName *string `json:"displayName,omitempty"`
}

// +kcc:proto=google.monitoring.v3.Snooze.Criteria
type Snooze_Criteria struct {
	// The specific `AlertPolicy` names for the alert that should be snoozed.
	//  The format is:
	//
	//      projects/[PROJECT_ID_OR_NUMBER]/alertPolicies/[POLICY_ID]
	//
	//  There is a limit of 16 policies per snooze. This limit is checked during
	//  snooze creation.
	// +kcc:proto:field=google.monitoring.v3.Snooze.Criteria.policies
	Policies []string `json:"policies,omitempty"`
}

// +kcc:proto=google.monitoring.v3.TimeInterval
type TimeInterval struct {
	// Required. The end of the time interval.
	// +kcc:proto:field=google.monitoring.v3.TimeInterval.end_time
	EndTime *string `json:"endTime,omitempty"`

	// Optional. The beginning of the time interval.  The default value
	//  for the start time is the end time. The start time must not be
	//  later than the end time.
	// +kcc:proto:field=google.monitoring.v3.TimeInterval.start_time
	StartTime *string `json:"startTime,omitempty"`
}
