package v1alpha1


// +kcc:proto=google.cloud.websecurityscanner.v1.Finding
type Finding struct {
	// Output only. The resource name of the Finding. The name follows the format of
	//  'projects/{projectId}/scanConfigs/{scanConfigId}/scanruns/{scanRunId}/findings/{findingId}'.
	//  The finding IDs are generated by the system.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Finding.name
	Name *string `json:"name,omitempty"`

	// Output only. The type of the Finding.
	//  Detailed and up-to-date information on findings can be found here:
	//  https://cloud.google.com/security-command-center/docs/how-to-remediate-web-security-scanner-findings
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Finding.finding_type
	FindingType *string `json:"findingType,omitempty"`

	// Output only. The http method of the request that triggered the vulnerability, in
	//  uppercase.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Finding.http_method
	HTTPMethod *string `json:"httpMethod,omitempty"`

	// Output only. The URL produced by the server-side fuzzer and used in the request that
	//  triggered the vulnerability.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Finding.fuzzed_url
	FuzzedURL *string `json:"fuzzedURL,omitempty"`

	// Output only. The body of the request that triggered the vulnerability.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Finding.body
	Body *string `json:"body,omitempty"`

	// Output only. The description of the vulnerability.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Finding.description
	Description *string `json:"description,omitempty"`

	// Output only. The URL containing human-readable payload that user can leverage to
	//  reproduce the vulnerability.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Finding.reproduction_url
	ReproductionURL *string `json:"reproductionURL,omitempty"`

	// Output only. If the vulnerability was originated from nested IFrame, the immediate
	//  parent IFrame is reported.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Finding.frame_url
	FrameURL *string `json:"frameURL,omitempty"`

	// Output only. The URL where the browser lands when the vulnerability is detected.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Finding.final_url
	FinalURL *string `json:"finalURL,omitempty"`

	// Output only. The tracking ID uniquely identifies a vulnerability instance across
	//  multiple ScanRuns.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Finding.tracking_id
	TrackingID *string `json:"trackingID,omitempty"`

	// Output only. An addon containing information reported for a vulnerability with an HTML
	//  form, if any.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Finding.form
	Form *Form `json:"form,omitempty"`

	// Output only. An addon containing information about outdated libraries.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Finding.outdated_library
	OutdatedLibrary *OutdatedLibrary `json:"outdatedLibrary,omitempty"`

	// Output only. An addon containing detailed information regarding any resource causing the
	//  vulnerability such as JavaScript sources, image, audio files, etc.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Finding.violating_resource
	ViolatingResource *ViolatingResource `json:"violatingResource,omitempty"`

	// Output only. An addon containing information about vulnerable or missing HTTP headers.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Finding.vulnerable_headers
	VulnerableHeaders *VulnerableHeaders `json:"vulnerableHeaders,omitempty"`

	// Output only. An addon containing information about request parameters which were found
	//  to be vulnerable.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Finding.vulnerable_parameters
	VulnerableParameters *VulnerableParameters `json:"vulnerableParameters,omitempty"`

	// Output only. An addon containing information reported for an XSS, if any.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Finding.xss
	Xss *Xss `json:"xss,omitempty"`
}

// +kcc:proto=google.cloud.websecurityscanner.v1.Form
type Form struct {
	// ! The URI where to send the form when it's submitted.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Form.action_uri
	ActionURI *string `json:"actionURI,omitempty"`

	// ! The names of form fields related to the vulnerability.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Form.fields
	Fields []string `json:"fields,omitempty"`
}

// +kcc:proto=google.cloud.websecurityscanner.v1.OutdatedLibrary
type OutdatedLibrary struct {
	// The name of the outdated library.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.OutdatedLibrary.library_name
	LibraryName *string `json:"libraryName,omitempty"`

	// The version number.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.OutdatedLibrary.version
	Version *string `json:"version,omitempty"`

	// URLs to learn more information about the vulnerabilities in the library.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.OutdatedLibrary.learn_more_urls
	LearnMoreUrls []string `json:"learnMoreUrls,omitempty"`
}

// +kcc:proto=google.cloud.websecurityscanner.v1.ViolatingResource
type ViolatingResource struct {
	// The MIME type of this resource.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.ViolatingResource.content_type
	ContentType *string `json:"contentType,omitempty"`

	// URL of this violating resource.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.ViolatingResource.resource_url
	ResourceURL *string `json:"resourceURL,omitempty"`
}

// +kcc:proto=google.cloud.websecurityscanner.v1.VulnerableHeaders
type VulnerableHeaders struct {
	// List of vulnerable headers.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.VulnerableHeaders.headers
	Headers []VulnerableHeaders_Header `json:"headers,omitempty"`

	// List of missing headers.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.VulnerableHeaders.missing_headers
	MissingHeaders []VulnerableHeaders_Header `json:"missingHeaders,omitempty"`
}

// +kcc:proto=google.cloud.websecurityscanner.v1.VulnerableHeaders.Header
type VulnerableHeaders_Header struct {
	// Header name.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.VulnerableHeaders.Header.name
	Name *string `json:"name,omitempty"`

	// Header value.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.VulnerableHeaders.Header.value
	Value *string `json:"value,omitempty"`
}

// +kcc:proto=google.cloud.websecurityscanner.v1.VulnerableParameters
type VulnerableParameters struct {
	// The vulnerable parameter names.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.VulnerableParameters.parameter_names
	ParameterNames []string `json:"parameterNames,omitempty"`
}

// +kcc:proto=google.cloud.websecurityscanner.v1.Xss
type Xss struct {
	// Stack traces leading to the point where the XSS occurred.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Xss.stack_traces
	StackTraces []string `json:"stackTraces,omitempty"`

	// An error message generated by a javascript breakage.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Xss.error_message
	ErrorMessage *string `json:"errorMessage,omitempty"`

	// The attack vector of the payload triggering this XSS.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Xss.attack_vector
	AttackVector *string `json:"attackVector,omitempty"`

	// The reproduction url for the seeding POST request of a Stored XSS.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Xss.stored_xss_seeding_url
	StoredXssSeedingURL *string `json:"storedXssSeedingURL,omitempty"`
}

// +kcc:proto=google.cloud.websecurityscanner.v1.Xxe
type Xxe struct {
	// The XML string that triggered the XXE vulnerability. Non-payload values
	//  might be redacted.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Xxe.payload_value
	PayloadValue *string `json:"payloadValue,omitempty"`

	// Location within the request where the payload was placed.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Xxe.payload_location
	PayloadLocation *string `json:"payloadLocation,omitempty"`
}

// +kcc:proto=google.cloud.websecurityscanner.v1.Finding
type FindingObservedState struct {
	// Output only. The severity level of the reported vulnerability.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Finding.severity
	Severity *string `json:"severity,omitempty"`

	// Output only. An addon containing information reported for an XXE, if any.
	// +kcc:proto:field=google.cloud.websecurityscanner.v1.Finding.xxe
	Xxe *Xxe `json:"xxe,omitempty"`
}
